export type SafeKeyOf<T> = keyof T extends never ? string : keyof T;
export type ResponsiveStyleValue<T> = T | Array<T | null> | { [key: string]: T | null };

export const findChild = (children: React.ReactNode, displayName: string) => Children.toArray(children).find(
  ch =>
    // eslint-disable-next-line @typescript-eslint/ban-ts-comment
    // @ts-ignore
    typeof ch === 'object' && displayName === ch.type.displayName,
);
export const findChildren = (children: React.ReactNode, displayName: string) => Children.toArray(children).filter(
  ch =>
    // eslint-disable-next-line @typescript-eslint/ban-ts-comment
    // @ts-ignore
    typeof ch === 'object' && displayName === ch.type.displayName,
);

export const childrenLength = (children: React.ReactNode) => Children.toArray(children).length;

