import { Accordion } from '../Accordion';
import React from 'react';
import Flex from "../Flex/Flex";
import {Box} from "../Box";
import Button from "../Button/Button";

const AccordionExample = () => {
  return (
    <>
      <Accordion variant="block">
        <Accordion.Summary>Title</Accordion.Summary>
        <Accordion.Details>Description</Accordion.Details>
      </Accordion>

      {/* if you dont want any arrow on the right side */}
      <Accordion variant="light">
        <Accordion.Summary expandIcon={undefined}>Title</Accordion.Summary>
        <Accordion.Details>Description</Accordion.Details>
      </Accordion>
      <Accordion variant="light" disabled>
        <Accordion.Summary>
          <Flex gap='sm-8'>
            <Box>
            You can put anything inside the summary
            </Box>
            <Button variant="plain">Button</Button>
          </Flex>
        </Accordion.Summary>
        <Accordion.Details>Any ReactNode can be contained in the details</Accordion.Details>
      </Accordion>
    </>

  );
};

