import {Flex} from "../Flex";

// Example of Divider usage
const Example = () => {

  return (
    <>
        <Flex direction='row' gap='sm-8' padding='sm-8'>
            <Divider showText={"center"} direction={"vertical"}>Text</Divider>
            <Divider showText={"top"} direction={"vertical"}>Text</Divider>
            <Divider showText={"bottom"} direction={"vertical"}>Text</Divider>
            <Divider direction={"vertical"}/>
        </Flex>

        {/* These two are equal */}
        <Divider children={"test"} direction={"horizontal"} showText={'none'}/>
        <Divider/>

        <Divider showText={"center"} direction={"horizontal"}>Text</Divider>
        <Divider showText={"left"} direction={"horizontal"}>Text</Divider>
        <Divider showText={"right"} direction={"horizontal"}>Text</Divider>
    </>
  );
};

