import React, {useState} from 'react';
import Feedback from './Feedback';
import {Flex} from "../Flex";
import {FeedbackValues} from "./Feedback.interface";

// Example of Feedback usage
const Example = () => {
    const [checkedEmoji, setCheckedEmoji] = useState<FeedbackValues<'emoji'>>('like')
    const [checkedThumb, setCheckedThumb] = useState<FeedbackValues<'thumb'>>(null)

    //the state is required for each Feedback

    return (
    <>
        <Flex gap={'md-8'}>
            <Flex direction="column" gap='sm-2'>
                <Feedback onChange={(value) => setCheckedEmoji(value)} value={checkedEmoji} size={'sm'} type={'emoji'}/>
                <Feedback type='emoji' size='md' value={null}/>
                <Feedback type='emoji' size='lg' value={null}/>
            </Flex>

            <Flex direction="column" gap='sm-2'>
                <Feedback onChange={(value) => setCheckedThumb(value)} value={checkedThumb} size={'sm'} type={'thumb'}/>
                <Feedback type='thumb' size='md' value={null}/>
                <Feedback type='thumb' size='lg' value={null}/>
            </Flex>
        </Flex>

    </>
  );
};

