import React from 'react';
import Multiselect from './Multiselect';
import Icon from '../Icon/Icon';
import Select from '../Select/Select';
import { Tag } from '../Tag';

// Example of Multiselect usage
const Example = () => {

  return (
    <>
      <Multiselect
        noOptionsText='No options'
        size='lg'
        options={['One', 'Two', 'Three']}
        name='numbers'
        label='Label'
      />

      <Multiselect
        noOptionsText='No users'
        size='lg'
        options={['John Doe', 'Jane Doe', 'Bruce Lee']}
        name='numbers'
        renderOption={(props, option, state, ownerState) => (
          <Select.Option {...props} startAddOn={<Icon name='User' />}>
            {ownerState.getOptionLabel(option)}
          </Select.Option >
        )}
        renderTags={(values, getTagProps, { getOptionLabel }) => {
          return values.map((value, index) => (
            <Tag size='md' icon={<Icon name='User' />} text={(getOptionLabel ? getOptionLabel(value) : value) as string} {...getTagProps({ index })} />
          ));
        }}
        label='Users'
      />
    </>
  );
};

