import * as React from 'react';

export interface RateType {
  star: string;
  heart: string;
}

export interface RateSize {
  xs: true;
  sm: true;
  md: true;
  lg: true;
  xl: true;
  xxl: true;
  xxxl: true;
}

export interface EmptyStyle {
  filled: string;
  outline: string;
}

export interface RateProps {
  /**
   * The value of the rating.
   */
  onChange: (event: React.SyntheticEvent, value: number | null) => void;
  /**
   * The type of the rating. It can be 'star' or 'heart'. Default is 'star'.
   */
  type?: SafeKeyOf<RateType>;
  /**
   * The size of the rating (e.g., 'xs', 'sm', 'md', 'lg', 'xl', 'xxl', 'xxxl').
   */
  size?: SafeKeyOf<RateSize>;
  /**
   * The style of the empty rating. It can be 'outline' or 'filled'. Default is 'outline'.
   */
  emptyStyle?: SafeKeyOf<EmptyStyle>;
  /**
   * The precision of the rating. Default is 0.5.
   */
  precision?: number;
  /**
   * The value of the rating.
   */
  defaultValue?: number;
}

// eslint-disable-next-line @typescript-eslint/no-empty-interface
