  top: true;
}

export type DimensionsProps = {
  width?: CSSProperties['width'];
  height?: CSSProperties['height'];
};

export interface BorderProps {
  border?: CSSProperties['border'];
  borderTop?: CSSProperties['borderTop'];
  borderRight?: CSSProperties['borderRight'];
  borderBottom?: CSSProperties['borderBottom'];
  borderLeft?: CSSProperties['borderLeft'];
}

export interface FlexChildProps {
  flex?: CSSProperties['flex'];
  flexBasis?: CSSProperties['flexBasis'];
  flexGrow?: CSSProperties['flexGrow'];
  flexShrink?: CSSProperties['flexShrink'];
  alignSelf?: CSSProperties['alignSelf'];
  order?: CSSProperties['order'];
}

export interface OverflowProps {
  overflow?: CSSProperties['overflow'];
}

// eslint-disable-next-line @typescript-eslint/ban-types
export type AnyOtherString = string & {};
export type SafeKeyOf<T> = keyof T extends never ? string : keyof T;
export type ResponsiveStyleValue<T> = T | Array<T | null> | { [key: string]: T | null };

export const findChild = (children: React.ReactNode, displayName: string) => Children.toArray(children).find(
  ch =>
    // eslint-disable-next-line @typescript-eslint/ban-ts-comment
    // @ts-ignore
    typeof ch === 'object' && displayName === ch.type.displayName,
);
export const findChildren = (children: React.ReactNode, displayName: string) => Children.toArray(children).filter(
  ch =>
    // eslint-disable-next-line @typescript-eslint/ban-ts-comment
    // @ts-ignore
    typeof ch === 'object' && displayName === ch.type.displayName,
);

export const childrenLength = (children: React.ReactNode) => Children.toArray(children).length;

