import React, { useState } from 'react';
import Select from './Select';

// Example of Select usage
const Example = () => {
  const [age, setAge] = useState<string | number | undefined>('');

  return (
    <>
      <Select value={age} onChange={(e) => setAge(e.target.value as any)} label='Label' name='select'>
        <Select.Option value={10}>Ten</Select.Option>
        <Select.Option value={20}>Twenty</Select.Option>
        <Select.Option value={30}>Thirty</Select.Option>
      </Select>
    </>
  );
};

