   * The content of the component: the text of the button.
   */
  children: string,
  /**
   * Callback function called when the button is clicked.
   */
  onClick: MouseEventHandler<HTMLButtonElement>
}

import React from 'react';
import {SafeKeyOf} from "../../utils";

export interface SidePanelFooterType {
    right: string;
    left: string;
    fullwidth: string;
}

export interface SidePanelFooterProps {
  /**
   * Whether to display a divider to separate the Content from the Footer.
   */
    divider?: boolean
  /**
   * SidePanel.PrimaryAction and SidePanel.SecondaryAction are allowed as children.
   */
    children: React.ReactNode
  /**
   * The type of the Footer (e.g, 'right', 'left' or 'fullwidth'). 'fullwidth' will make the Footer span the entire width of the SidePanel.
   */
    type: SafeKeyOf<SidePanelFooterType>;
}

import {ReactNode} from "react";

export interface SidePanelHeaderProps {
  /**
   * The title of the SidePanel.
   */
    title: string
  /**
   * Other components to be rendered in the header.
   */
    children?: ReactNode;
}

import React, {MouseEventHandler} from "react";
import {SafeKeyOf} from "../utils";

interface SidePanelAnchor {
    bottom: true
    left: true
    right: true
    top: true
}

export interface SidePanelProps {
  /**
   * Where the SidePanel should be displayed (e.g., 'left', 'right', 'top', 'bottom')
   */
    anchor?: SafeKeyOf<SidePanelAnchor>
  /**
   * Whether the SidePanel is open or not
   */
    open?: boolean;
  /**
   * Callback fired when the component requests to be closed
   */
  onClose?: MouseEventHandler<HTMLButtonElement>
  /**
   * SidePanel.Header, SidePanel.Content, SidePanel.Footer are allowed as children. SidePanel.Content is required.
   */
    children: React.ReactNode
}

