export interface TickMarkType {
  onlyMarks: string,
  percentage: string,
  dollars: string,
  everyTenUnits: string
}

interface BaseSliderProps {
  /**
   * Name attribute of the input element.
   */
  name: string
  /**
   * If true, the slider will be disabled.
   */
  disabled?: boolean
  /**
   * The tick mark type. It can be 'percentage', 'dollars', 'everyTenUnits' or undefined. If undefined, no tick marks will be displayed.
   */
  tickMark?: SafeKeyOf<TickMarkType>
  /**
   * The granularity with which the slider can step through values.
   */
  step?: number
  /**
   * The minimum allowed value of the slider.
   */
  min?: number
  /**
   * The maximum allowed value of the slider.
   */
  max?: number
}

interface SimpleSliderProps extends BaseSliderProps {
  /**
   * The type of the slider. It can be 'slider' or 'range'.
   */
  type: 'slider'
  /**
   * The value of the slider. When the slider type is set to 'range' provide an array with two values. Otherwise, provide a number.
   */
  value: number
  /**
   * Callback fired when the value changes. It receives the value as a number when the slider type is set to 'slider' or an array of two numbers when the slider type is set to 'range'.
   */
  onChange: (value: number) => void
}

interface RangeProps extends BaseSliderProps {
  /**
   * The type of the slider. It can be 'slider' or 'range'.
   */
  type: 'range';
  /**
   * The value of the slider. When the slider type is set to 'range' provide an array with two values. Otherwise, provide a number.
   */
  value: [number, number];
  /**
   * Callback fired when the value changes. It receives the value as a number when the slider type is set to 'slider' or an array of two numbers when the slider type is set to 'range'.
   */
  onChange: (value: [number, number]) => void
}

