import React, {useState} from 'react';
import Slider from './Slider';

// Example of Slider usage
const Example = () => {
  const [slider, setSlider] = useState<number>(0);
  const [range, setRange] = useState<[number, number]>([1, 2]);

  return (
    <>
      <Slider type='range' name='range' label='Range' labelProps={{size: 'sm', required: true}} tickMark='percentage' value={range} onChange={(value) => setRange(value)}/>
      <Slider type='slider' name='slider' label='SliderDollars' tickMark='dollars' min={100} max={10000} value={slider} onChange={(value) => setSlider(value)}/>
    </>
  );
};

