import React from 'react';
import { Row, SortingRule } from 'react-table';
import TableRow from '@mui/material/TableRow';
import { ColumnsDefinition } from './types';

export interface TableProps<T extends object> {
  /**
   * An array of objects that will be rendered as rows.
   */
  data: T[];
  /**
   * An array of objects that defines the columns of the table.
   */
  columnsDefinition: ColumnsDefinition<T>;
  /**
   * A React node that will be rendered when there are no items to show.
   */
  noItemsPlaceholder: React.ReactNode;
  /**
   * A function that receives a row and returns a string that will be used as the key of the row.
   */
  keyExtractor: (data: T) => string | number;
  /**
   * The test id of the table.
   */
  listTestId?: string;
  /**
   * The number of items that will be rendered before the scroll is activated.
   */
  itemsToShowBeforeScroll?: number;
  /**
   * A boolean that indicates if the table is loading. If it is true, a loading bar will be rendered.
   */
  loading?: boolean;
  /**
   * An object that contains the following properties:
   */
  infiniteLoader?: {
    /**
     * A function that receives an index and returns a boolean that indicates if the item is loaded.
     */
    isItemLoaded: (index: number) => boolean;
    /**
     * A function that will be called when the user scrolls to the bottom of the table.
     */
    loadMoreItems: (startIndex: number, stopIndex: number) => void;
    /**
     * The number of items that will be rendered before the loadMoreItems function is called.
     */
    threshold?: number;
  };
  /**
   * A boolean that indicates if the sorting is done in the server side.
   */
  serverSideSorting?: boolean;
  /**
   * An array of objects that defines the initial sorting of the table.
   */
  initialSort?: {
    /**
     * The id of the column.
     */
    id: string;
    /**
     * A boolean that indicates if the sorting is descending.
     */
    desc: boolean;
  }[];
  /**
   * A function that will be called when the sorting strategy changes.
   */
  onSortChange?: (sorting: SortingRule<T>[]) => void;
  /**
   * A React node that will be rendered instead of the default header.
   */
  customHeaderContent?: React.JSXElementConstructor<any> | undefined;
  /**
   * A function that receives a row and returns a string that will be used as the test id of the row.
   */
  rowTestIdBuilder?: (data: T) => string;
/**
   * The height of the rows.
   */
  rowHeight?: number;
  /**
   * An object that will be spread into the rows of the table to override (some of) their props.
   */
  rowPropsOverride?: Partial<React.ComponentProps<typeof TableRow>>;
  /**
   * A function that will be called when the user clicks on a row.
   */
  onRowClick?: (data: T, index: number) => void;
  /**
   * A function that will be called when the user selects a row.
   */
  onRowsSelected?: (selectedRows: Row<T>[]) => void;
}

