import React, { useMemo } from 'react';
import Table from './Table';
import { ColumnsDefinition } from './types';
import { Typography } from '../Typography';
import { Dropmenu, useDropmenu } from '../Dropmenu';
import { Button } from '../Button';
import { Icon } from '../Icon';

type Data = { user: string; nickname: string; email: string };
export const columnsDefinition: (tr: any) => ColumnsDefinition<Data> = ({ translate, dateTimeFormat }) => ([
  {
    accessor: 'user',
    Header: () => 'User',
    Cell: ({ rowData }) => {
      const { user, nickname } = rowData;

      return (
        <>
          <Typography variant='body/sm/regular'>{user}</Typography>
          <Typography variant='body/xs/regular' color="grey.11">
            {nickname}
          </Typography>
        </>
      );
    },
    align: 'start',
    sortable: true,
  },
  {
    accessor: 'email',
    Header: () => 'Email',
    Cell: ({ rowData }) => rowData.email,
    align: 'start',
  },
  {
    id: 'actions',
    accessor: () => null,
    Header: () => '',
    Cell: ({ rowData }) => {
      const { anchorEl, isOpen, openDropmenu, closeDropmenu } = useDropmenu();

      return (
        <div>
          <Button onClick={openDropmenu} size='sm' variant='outlined' color='grey' icon={<Icon name='MoreVertical' />} onlyIcon />

          <Dropmenu open={isOpen} anchorEl={anchorEl} onClose={closeDropmenu}>
            <Dropmenu.Header>Header</Dropmenu.Header>
            <Dropmenu.Item onClick={() => ''} size='sm' startAddOn={<Icon name='Home' />} endAddOn='AAAA' value={10}>Ten</Dropmenu.Item>
            <Dropmenu.Item onClick={() => ''} size='sm' value={20}>Twenty</Dropmenu.Item>
            <Dropmenu.Item disabled onClick={() => ''} size='sm' value={30}>Thirty</Dropmenu.Item>
            <Dropmenu.Footer>Footer</Dropmenu.Footer>
          </Dropmenu>
        </div>
      )
    },
    align: 'end',
  },
]);


// Example of Table usage
const Example = () => {
  // const t = useTranslate();
  // const d = useDateTimeFormat();

  // NOTE: columns definition always has to be stable
  const cols = useMemo(() => {
    return columnsDefinition({ translate: () => '', dateTimeFormat: () => '' });
    // TODO: include t and d translation funcs in deps array
  }, []);

  return (
    <>
      <Table
        columnsDefinition={cols}
        data={[
          { user: 'John Doe', nickname: '@John', email: 'john.doe@gmail.com' },
          { user: 'Jane Doe', nickname: '@Jane', email: 'jane.doe@gmail.com' },
        ]}
        keyExtractor={c => c.user}
        loading={false}
        onRowClick={() => 'click'}
        itemsToShowBeforeScroll={8}
        initialSort={[{ id: 'user', desc: true }]}
        noItemsPlaceholder='No users'
      />
    </>
  );
};

