import { MouseEventHandler, ReactNode } from 'react';
import { State, Size, ThemeColor, SafeKeyOf } from '../utils';
import { SxProps, Theme } from '@mui/material';

type ButtonColor = SafeKeyOf<ThemeColor>;
type ButtonSize = SafeKeyOf<Size>;

// Button states: hovered, pressed, focused, disabled, loading
// eslint-disable-next-line @typescript-eslint/no-empty-interface
export interface ButtonState extends Omit<State, 'error'> {}

/**
 * @interface ButtonVariant
 * @member {true} contained corresponds to the 'filled' Figma variant
 * @member {true} text corresponds to the 'plain' Figma variant
 */
export interface ButtonVariant {
  contained: true;
  outlined: true;
  tinted: true;
  text: true;
}

export interface BaseButtonProps extends ButtonState {
  testId?: string;
  type?: 'button' | 'submit' | 'reset';
  onClick?: MouseEventHandler<HTMLButtonElement>;
  children?: string;
  variant?: SafeKeyOf<ButtonVariant>;
  color?: ButtonColor;
  size?: ButtonSize;
  onlyIcon?: true;
  icon?: ReactNode;
  leftIcon?: ReactNode;
  rightIcon?: ReactNode;
  style?: SxProps<Theme>;
  className?: string;
}

export interface SimpleButtonProps extends BaseButtonProps {
  children: string;
  onlyIcon?: undefined;
  icon?: undefined;
}

export interface IconButtonProps extends BaseButtonProps {
  children?: undefined;
  onlyIcon: true;
  icon: ReactNode;
  leftIcon?: undefined;
  rightIcon?: undefined;
}

