import React, { useState } from 'react';
import { Icon } from '../Icon';
import Tabs from './Tabs';

// Example of Tabs usage
const Example = () => {
  const [currentTab, setCurrentTab] = useState(0);

  return (
    <>
      <Tabs size='sm' currentTab={currentTab} fullWidth onChange={(_, t) => setCurrentTab(t)}>
        <Tabs.Tab icon={<Icon name='Home' />} value={0}>Home</Tabs.Tab>
        <Tabs.Tab value={1}>Profile</Tabs.Tab>
        <Tabs.Tab value={2}>Settings</Tabs.Tab>
      </Tabs>

      <Tabs orientation='vertical' size='sm' currentTab={currentTab} onChange={(e, t) => setCurrentTab(t)}>
        <Tabs.Tab icon={<Icon name='Home' />} value={0}>Home</Tabs.Tab>
        <Tabs.Tab value={1}>Profile</Tabs.Tab>
        <Tabs.Tab value={2}>Settings</Tabs.Tab>
      </Tabs>

      {[0, 1, 2].map((index) => {
        return (
          <Tabs.Panel index={index} currentTab={currentTab}>
            {`Tab Content: ${index}`}
          </Tabs.Panel>
        )
      })}
    </>
  );
};

